import React,{useState,useEffect} from 'react'
import axios from 'axios'

const ProductList = () => {
  const [products,SetProducts] = useState([])
  const fetchProducts = async () => {
    try{
      const response = await axios.get('http://localhost:5000/readAllProducts')
      SetProducts(response.data)
    }catch (error) {
      console.log(error)
    }
  }
  const deleteHandler = async(id)=>{
    try{
      const response = await axios.delete(`http://localhost:5000/delete/${id}`)
      console.log(response)
      fetchProducts();
    } catch (error){
      console.log(error)
    }
    
  }

  useEffect(() =>{
    fetchProducts()
  },[])
  return (
    <>
  
<button type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#staticBackdrop">
  Launch static backdrop modal
</button>


<div class="modal fade" id="staticBackdrop" data-bs-backdrop="static" data-bs-keyboard="false" tabindex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h1 class="modal-title fs-5" id="staticBackdropLabel">Modal title</h1>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        ...
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Understood</button>
      </div>
    </div>
  </div>
</div>
    <div className='mt-3'>
      <h1>Product List</h1>
      <ul className='list-group'>
      {
        products.map((product)=>{
          return <li class="list-group-item list-group-item-success"> product:{product.name}  price:{product.price} <i class="fa-regular fa-pen-to-square"></i> <i class="fa-solid fa-trash" onClick={()=>{deleteHandler(product._id)}}></i></li>
        })
      }
    </ul>
    </div>
    </>
  )
}

export default ProductList
